    //                             <div className="cart ">
    //                                 <h3>{item.city} </h3>
    //                                 {
    //                                     item.image.map((img, index) => (
    //                                         <div key={index}>
    //                                             <img src={img} alt={item} className='image w-25' ></img>
    //                                             <button className="btn bg-info w-50 my-3">Per sqft : {item.rate} </button>
    //                                         </div>
    //                                     ))
    //                                 }
    //                             </div>
    //                         </div>
    //                     ))
    //                 }
    //             </div>
    //         </>
    //     )
    // }

    const cartImage = (API) => {
        fetch('http://localhost:3333/realestate/cartimage')
            .then(async (res) => {
                const cart = await res.json();
                return cart;
            })
            .then((cart) => {
                console.log("Image data : ", cart.data);
                setShowCart(cart.data);
            })
            .catch((err) => {
                console.log("Cart image: ", err.message);
            })
    }

    // Image show
    const imageMap = (item, index) => {
        return (
            <>
                <div key={index}>
                    <CardMedia
                        component="img"
                        alt="green iguana"
                        height="140"
                        image={item}
                    />
                </div>
            </>
        )
    }

    const DynamicCarty = () => {
        return (
            <div className="cartD">
                {
                    showCart.map((item, index) => (
                        <div key={index}>
                            <Card sx={{ maxWidth: 345 }}>
                                {
                                    item.image.map((item, index) => (
                                        index === 0 ? imageMap(item, index) : null
                                    ))
                                }
                                <CardContent>
                                    <Typography gutterBottom variant="h5" component="div">
                                        {item.city}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        {item.info}
                                    </Typography>
                                </CardContent>
                                <CardActions>
                                    <Button size="small" variant="contained" color="success" >{item.propertyFor} </Button>
                                    <Button size="small" variant="contained" color="primary">{item.type} </Button>
                                </CardActions>
                            </Card>
                        </div>
                    ))
                }
            </div >
        );
    }



    // const tryCart = () => {
    //     return (
    //         <>
    //             <div className="cartShow bg-dark">
    //                 {
    //                     showCart.map((item, index) => (
    //                         <div key={index}>
    //                             {
    //                                 item.image.map((img, index) => (
    //                                     <div key={index} className='image'>
    //                                         <img src={img} alt='No property upload'></img>
    //                                     </div>
    //                                 ))
    //                             }
    //                         </div>
    //                     ))
    //                 }
    //             </div>
    //         </>
    //     )
    // }

    useEffect((e) => {
        cartImage();
    }, [])

    return (
        <React.Fragment>
            {/* {propertyCart()} */}
            {/* {tryCart()} */}
            {material()}
        </React.Fragment>
    )
}

export default PropertyCart;
